index.html<pre><code>&lt;!DOCTYPE html&gt;
&lt;html&gt;
&lt;head&gt;
    &lt;meta charset="utf-8"&gt;    
    &lt;script src="https://cesium.com/downloads/cesiumjs/releases/1.123/Build/Cesium/Cesium.js"&gt;&lt;/script&gt;
    &lt;link href="https://cesium.com/downloads/cesiumjs/releases/1.123/Build/Cesium/Widgets/widgets.css" rel="stylesheet"&gt;
&lt;/head&gt;

&lt;body onload="render()"&gt;
  &lt;div id="cesiumContainer"&gt;&lt;/div&gt;
  &lt;div id="toolbar"&gt;&lt;/div&gt;
  &lt;script&gt;
    async function render()
    {
      Cesium.Ion.defaultAccessToken = "토큰";

      const viewer = new Cesium.Viewer("cesiumContainer", {
        terrainProvider: await Cesium.CesiumTerrainProvider.fromIonAssetId(1)
      });

      viewer.scene.globe.depthTestAgainstTerrain = true;

      try 
      {
        // 애셋을 추가한다.
        var tileset = await Cesium.Cesium3DTileset.fromIonAssetId(96188);
        viewer.scene.primitives.add(tileset);

        // 시계탑
        tileset = await Cesium.Cesium3DTileset.fromIonAssetId(시계탑ID);
        viewer.scene.primitives.add(tileset);

        // 또 다른 asset
        tileset = await Cesium.Cesium3DTileset.fromIonAssetId(애셋ID);
        viewer.scene.primitives.add(tileset);

        await viewer.scene.camera.setView({
          destination: Cesium.Cartesian3.fromDegrees(127.0, 36.5, 300), // 위도, 경도, 높이
          orientation: new Cesium.HeadingPitchRoll.fromDegrees(0, -10, 0,), // Heading(Yaw), Pitch, Roll(bank)
        });
      } 
      catch (error) 
      {
        console.log(error);
      }
    }
  &lt;/script&gt;
&lt;/body&gt;
&lt;/html&gt;
</code></pre>
